import React from "react";
import './Button.css';

export default props => {

    const classes = setClasses()

    return (
        <button className={classes}>
            {props.label}
        </button>
    )
}

function setClasses() {
    let classes = 'button '
    classes += props.operation ? 'operation' : ''
    classes += props.double ? 'double': ''
    classes += props.triple ? 'triple': ''

    return classes
}
